import React, { useEffect, useState } from 'react'
import { Image, TextInput, View } from 'react-native'
import * as ImagePicker from 'expo-image-picker'

import Screen from './app/components/Screen'

//partOne
import Welcome from './app/screens/1/Welcome'
import Login from './app/screens/1/Login'
import Register from './app/screens/1/Register'

//partTwo
import Home from './app/screens/2/Home'
import Search from './app/screens/2/Search'
import SearchResults from './app/screens/2/SearchResults'
import ShoeFocus from './app/screens/2/ShoeFocus'
import ViewImage from './app/screens/2/ViewImage'
import AppButton from './app/components/AppButton'
import ImageInput from './app/components/ImageInput'

//partThree
// import ViewProfile from './app/screens/3/ViewProfile'

export default function App() {
  const [imageUri, setImageUri] = useState()


  // request access from use for Sneaked to access camera
  const requestPermission = async () => {
    const result = await ImagePicker.requestCameraPermissionsAsync
    if (!result.granted) {
      alert('You need to enable permission to access the libray')
    }
  }

  // loads the permission for camera use
  useEffect(() => {
    requestPermission()
  }, [])

  const selectImage = async () => {
    try {
      const result3 = await ImagePicker.launchImageLibraryAsync()
      if (!result3.cancelled) {
        setImageUri(result3.uri)
      }

    } catch (error) {
      console.log('Error reading image', error)
    }
  }


  return <Screen>
    <AppButton title="Select Image" onPress={selectImage}/>
    <Image source={{uri:imageUri}}/>
    <ImageInput />
  </Screen>
}
